<?php

namespace App\Tests\Leetcode;

use App\Leetcode\LongestPalindromicSubstring;
use PHPUnit\Framework\TestCase;

class LongestPalindromicSubstringTest extends TestCase
{
    public static function longestPalindromeProvider(): array
    {
        return [
            [
                'input' => 'babad',
                'expectedOutput' => 'bab',
            ],
            [
                'input' => 'babadab',
                'expectedOutput' => 'badab',
            ],
            [
                'input' => 'cbbd',
                'expectedOutput' => 'bb',
            ],
            [
                'input' => '',
                'expectedOutput' => '',
            ],
        ];
    }

    /**
     * @dataProvider longestPalindromeProvider
     */
    public function testLongestPalindrome(string $input, string $expectedOutput): void
    {
        $solution = new LongestPalindromicSubstring();
        self::assertSame($expectedOutput, $solution->longestPalindromeV2($input));
    }
}
